mapper.update:
UPDATE token
SET accessToken = #{accessToken}, isLogin = 1
WHERE userId = #{userId};

mapper.select:
SELECT accessToken
FROM token
WHERE userId = #{userId} AND isLogin = 1;

mapper.update:
UPDATE token
SET isLogin = 0
WHERE userId = #{userId};

mapper.select:
SELECT token
FROM token
WHERE userId = #{userId};

SELECT accessToken
FROM token
where accessToken = #{accessToken} AND userId=#{userId};
