mapper.select:
SELECT userId, userEmail, userName, isAdmin, isLogin
FROM users
WHERE userEmail = #{userEmail} AND password = #{password}

SELECT userEmail, userName, isAdmin
FROM users
WHERE userId = #{userId}

SELECT COUNT(*)
FROM users
WHERE userEmail = #{userEmail}

SELECT COUNT(*)
FROM users
WHERE userName = #{userName}

SELECT *
FROM users
WHERE isAdmin = #{isAdmin}
ORDER BY userId DESC
LIMIT #{start}, #{size}

SELECT COUNT(*)
FROM users
WHERE isAdmin = #{isAdmin}
